export interface TdActionSheetProps {
    cancelText?: string;
    count?: number;
    items: Array<string | ActionSheetItem>;
    showCancel?: boolean;
    theme?: 'list' | 'grid';
    visible: boolean;
    defaultVisible: boolean;
    modelValue: boolean;
    onCancel?: (context: {
        e: MouseEvent;
    }) => void;
    onClose?: (context: {
        e: MouseEvent;
    }) => void;
    onSelected?: (selected: ActionSheetItem | String, index: number) => void;
}
export interface ActionSheetItem {
    label: string;
    color?: string;
    disabled?: boolean;
}
