    placeholder?: string;
    shape?: 'square' | 'round';
    value?: string;
    defaultValue?: string;
    modelValue?: string;
    onActionClick?: (context: {
        e: MouseEvent;
    }) => void;
    onBlur?: (value: string, context: {
        e: FocusEvent;
    }) => void;
